import React from 'react';
import styled from 'styled-components';

const StyledFeatureInfo = styled.div`
  width: 28vw;
  height: 28vw;
  background: #F4F4F4;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

function FeatureInfo({title, description, svgImg}) {
  return (
    <StyledFeatureInfo className="feature-info">
      {svgImg && <svgImg />}
      <span>{title}</span>
      <span>{description}</span>
    </StyledFeatureInfo>
  );
}

export default FeatureInfo;
