import React, { useState } from 'react';
import styled from 'styled-components';
import Plx from 'react-plx';
import backArrow from '../backArrow.svg';
import fowardArrow from '../forwardArrow.svg';
import mpg from '../mpg.svg';
import hp from '../hp.svg';
import sixty from '../sixty.svg';

import {
  vehicleSlideShowPlx,
  slideTextPlx,
  vehiclePicPlx
} from '../plxSettings'

const StyledCarInfoDisplay = styled.div`
  position: absolute;
  right: 60vw;
  bottom: 40px;
  width: 35vw;
  height: 75vh;

  .title-text{
    display: flex;
    flex-direction: column;
    font-size: 70px;
    position: absolute;
    top: 8vh;
    right: -48vw;
    width: 40vw;
    color: #2C3245;
    font-family: 'Bebas Neue';
  }

  .desc-text-block {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 45vw;
    height: 60px;
    font-family: 'Helvetica';
    font-size: 11px;
    position: absolute;
    top: 35vh;
    right: -53vw;
  }

  .arrow-wrapper {
    position: absolute;
    top: 20px;
    right: 15px;
    display: flex;
    flex-direction: row;
  }
  .arrow {
    height: 7px;
    padding: 10px;
  }

  .slide-show-plx {
    transform-origin: 100% 50%;
    width: 100%;
    height: 100%;

    .slide-show-background {
      background: #FAC943;
      width: 100%;
      height: 100%;
    }
  }

  .slide-text-plx {
    position: absolute;
    top: 0;
    right: 0;
    opacity: 0;

    .car-text {
      display: flex;
      flex-direction: column;
      font-family: 'Bebas Neue';
      position: absolute;
      left: -25vw;
      top: 14vh;

      #car-name {
        font-size: 50px;
      }

      #car-model-year {
        color: #56534F;
        font-size: 13px;
        margin-top: -10px;
      }

      #all-stats {
        margin-top: 40px;
        height: 100px;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
      }
      .car-info-piece {
        width: 100px;
        height: 25px;
        display: flex;
        justify-content: space-between;
        align-items: center;

        #icon-holder {
          display: flex;
          justify-content: flex-start;
          align-items: center;
        }
      }

      .desc-car {
        font-size: 12px;
      }
      #mpg-text {
        margin-left: -25px;
      }
      #hp-text {
        margin-left: -16px;
      }
      #sixty-text {
        margin-left: -22px;
      }
    }

    button {
      z-index: 1;
    }
  }

  .vehicle-pic-plx {
    opacity: 0;

    #vehicle-img-1 {
      position: absolute;
      bottom: -54px;
      right: -258px;
      transform: scaleX(-1);
      height: 23vh;
    }

    #vehicle-img-2 {
      position: absolute;
      position: absolute;
      bottom: -66px;
      right: -285px;
      height: 34vh;
    }
  }
`;

function CarInfoDisplay() {
  const [selectedVehicleIndex, setSelectedVehicle] = useState(0);
  const vehicles = [
    {
      id: '1',
      name: 'Porsche',
      year: '2019',
      model: '911 Carrera S',
      mpg: '19/24',
      hp: '443',
      zeroToSixty: '3.2',
      img: 'images/silver_car.png',
    },
    {
      id: '2',
      name: 'Land Rover',
      year: '2019',
      model: 'Range Rover Velar',
      mpg: '19/24',
      hp: '397',
      zeroToSixty: '4.2',
      img: 'images/silver_suv.png',
    },
  ];

  const nextVehicle = () => {
    const nextIndex = selectedVehicleIndex + 1;
    const indexToUse = nextIndex === vehicles.length ? 0 : nextIndex;
    setSelectedVehicle(indexToUse);
  };

  const prevVehicle = () => {
    const prevIndex = selectedVehicleIndex - 1;
    const indexToUse = prevIndex < 0 ? vehicles.length - 1 : prevIndex;
    setSelectedVehicle(indexToUse);
  };

  const selectedVehicle = vehicles[selectedVehicleIndex];
  const imageText = `${selectedVehicle.name} - ${selectedVehicle.model}`;

  return (

      <StyledCarInfoDisplay className="car-information">
        <Plx
          className='slide-show-plx'
          parallaxData={vehicleSlideShowPlx}
        >
          <div className='slide-show-background' />
        </Plx>
        <Plx
          className='slide-text-plx'
          parallaxData={slideTextPlx}
        >
          <div className='arrow-wrapper'>
            <img
              src={backArrow}
              className='arrow'
              id='back-arrow'
              alt='back-arrow'
              onClick={prevVehicle}
            />
            <img
              src={fowardArrow}
              className='arrow'
              id='forward-arrow'
              alt='forward-arrow'
              onClick={nextVehicle}
            />
          </div>
          <div>
            <div id='icon-holder'>
              <img
                src={mpg}
                className='mpg-icon'
                alt='gas-tank'
              />
              <span className='desc-car' id='mpg-text'>MPG</span>
            </div>
            <div id='icon-holder'>
              <img
                src={sixty}
                className='sixty-icon'
                alt='speedometer'
              />
              <span className='desc-car' id='sixty-text'>0-60</span>
            </div>
            <div id='icon-holder'>
              <img
                src={hp}
                className='hp-icon'
                alt='graph'
              />
              <span className='desc-car' id='hp-text'>HP</span>
            </div>
          </div>

          <div className='car-text'>
            <span id='car-name'>{selectedVehicle.name}</span>
            <span id='car-model-year'>{`${selectedVehicle.year}-${selectedVehicle.model}`}</span>
          <div id='all-stats'>
            {/* <div className='car-info-piece'>

              <span>{selectedVehicle.mpg}</span>
            </div>

            <div className='car-info-piece'>

              <span>{selectedVehicle.hp}</span>
            </div>

            <div className='car-info-piece'>

              <span>{selectedVehicle.zeroToSixty}</span>
            </div> */}

          </div>

          </div>
        </Plx>
        <Plx
          className='vehicle-pic-plx'
          parallaxData={vehiclePicPlx}
          >
          <img
            className='vehicle-img'
            id={`vehicle-img-${selectedVehicle.id}`}
            src={selectedVehicle.img}
            alt={imageText}
          />
        </Plx>
        <div className='title-text'>
          <span>SELECT A VEHICLE</span>
          <span>FROM YOUR PHONE.</span>
        </div>
        <div className='desc-text-block'>
          <span>We have three tiers of luxary vehicles to choose from, each with all-inclusive pricing.</span>
          <span>Once you've picked the car you want to Drive from our lineup, all we need is a driver's</span>
          <span>license and an address to deliver the car. No gimmicks. No hidden fees.</span>
        </div>
      </StyledCarInfoDisplay>
  );
}

export default CarInfoDisplay;
