import React, { useState } from 'react';
import styled from 'styled-components';
import Plx from 'react-plx';
import backArrow from '../backArrow.svg';
import fowardArrow from '../forwardArrow.svg';
import mpg from '../mpg.svg';
import hp from '../hp.svg';
import sixty from '../sixty.svg';

import {
  vehicleSlideShowPlx,
  slideTextPlx,
  vehiclePicPlx
} from '../plxSettings'

const StyledCarInfoDisplay = styled.div`
  position: absolute;
  right: 60vw;
  bottom: 40px;
  width: 35vw;
  height: 75vh;

  .arrow-wrapper {
    position: absolute;
    top: 20px;
    right: 15px;
    display: flex;
    flex-direction: row;
  }
  .arrow {
    height: 7px;
    padding: 10px;
  }

  .slide-show-plx {
    transform-origin: 100% 50%;
    width: 100%;
    height: 100%;

    .slide-show-background {
      background: #FAC943;
      width: 100%;
      height: 100%;
    }
  }

  .slide-text-plx {
    position: absolute;
    top: 0;
    right: 0;
    opacity: 0;

    .car-text {
      display: flex;
      flex-direction: column;
      font-family: 'Bebas Neue';
      position: absolute;
      left: -25vw;
      top: 14vh;

      #car-name {
        font-size: 50px;
      }

      #car-model-year {
        color: #56534F;
        font-size: 13px;
        margin-top: -10px;
      }

      #all-stats {
        margin-top: 40px;
        height: 100px;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
      }
      .car-info-piece {
        width: 100px;
        height: 25px;
        display: flex;
        justify-content: space-between;
        align-items: center;

        #icon-holder {
          display: flex;
          justify-content: flex-start;
          align-items: center;
        }
      }
    }

    button {
      z-index: 1;
    }
  }

  .vehicle-pic-plx {
    opacity: 0;

    #vehicle-img-1 {
      position: absolute;
      bottom: -54px;
      right: -258px;
      transform: scaleX(-1);
      height: 23vh;
    }

    #vehicle-img-2 {
      position: absolute;
      position: absolute;
      bottom: -66px;
      right: -285px;
      height: 34vh;
    }
  }
`;

function CarInfoDisplay() {
  const [selectedVehicleIndex, setSelectedVehicle] = useState(0);
  const vehicles = [
    {
      id: '1',
      name: 'Porsche',
      year: '2019',
      model: '911 Carrera S',
      mpg: '19/24',
      hp: '443',
      zeroToSixty: '3.2',
      img: 'images/silver_car.png',
    },
    {
      id: '2',
      name: 'Land Rover',
      year: '2019',
      model: 'Range Rover Velar',
      mpg: '19/24',
      hp: '397',
      zeroToSixty: '4.2',
      img: 'images/silver_suv.png',
    },
  ];

  const nextVehicle = () => {
    const nextIndex = selectedVehicleIndex + 1;
    const indexToUse = nextIndex === vehicles.length ? 0 : nextIndex;
    setSelectedVehicle(indexToUse);
  };

  const prevVehicle = () => {
    const prevIndex = selectedVehicleIndex - 1;
    const indexToUse = prevIndex < 0 ? vehicles.length - 1 : prevIndex;
    setSelectedVehicle(indexToUse);
  };

  const selectedVehicle = vehicles[selectedVehicleIndex];
  const imageText = `${selectedVehicle.name} - ${selectedVehicle.model}`;

  return (

      <StyledCarInfoDisplay className="car-information">
        <Plx
          className='slide-show-plx'
          parallaxData={vehicleSlideShowPlx}
        >
          <div className='slide-show-background' />
        </Plx>
        <Plx
          className='slide-text-plx'
          parallaxData={slideTextPlx}
        >
          <div className='arrow-wrapper'>
            <img
              src={backArrow}
              className='arrow'
              id='back-arrow'
              alt='back-arrow'
              onClick={prevVehicle}
            />
            <img
              src={fowardArrow}
              className='arrow'
              id='forward-arrow'
              alt='forward-arrow'
              onClick={nextVehicle}
            />
          </div>
          <div className='car-text'>
            <span id='car-name'>{selectedVehicle.name}</span>
            <span id='car-model-year'>{`${selectedVehicle.year}-${selectedVehicle.model}`}</span>
          <div id='all-stats'>
            <div className='car-info-piece'>
              <div id='icon-holder'>
                <img
                  src={mpg}
                  className='mpg-icon'
                  alt='gas-tank'
                />
                <span>MPG</span>
              </div>
              <span>{selectedVehicle.mpg}</span>
            </div>

            <div className='car-info-piece'>
              <div id='icon-holder'>
                <img
                  src={hp}
                  className='hp-icon'
                  alt='graph'
                />
                <span>HP</span>
              </div>
              <span>{selectedVehicle.hp}</span>
            </div>

            <div className='car-info-piece'>
              <div id='icon-holder'>
                <img
                  src={sixty}
                  className='sixty-icon'
                  alt='speedometer'
                />
                <span>0-60</span>
              </div>
              <span>{selectedVehicle.zeroToSixty}</span>
            </div>

          </div>

          </div>
        </Plx>
        <Plx
          className='vehicle-pic-plx'
          parallaxData={vehiclePicPlx}
          >
          <img
            className='vehicle-img'
            id={`vehicle-img-${selectedVehicle.id}`}
            src={selectedVehicle.img}
            alt={imageText}
          />
        </Plx>
      </StyledCarInfoDisplay>
  );
}

export default CarInfoDisplay;
