import SlideOne from './slides/SlideOne';

function App() {

  const [elementPosition, setElementPosition] = useState({ x: 20, y: 150 })
  const elementRef = useRef()

  // Element scroll position
  useScrollPosition(
    ({ currPos }) => {
      setElementPosition(currPos)
    }, [], elementRef
  )

  console.log(elementPosition)
  return (
    <div className="App">
      <SlideOne />
      {/* <SlideTwo />
